"use strict"

const YAML = require('node-yaml');

module.exports = {
    parse: function (content){
        var dictContent = content;

        try{
            dictContent = JSON.parse(content)
        }
        catch(e){

            //try YAML instead
            try{
                dictContent = YAML.parse(content)
            }
            catch(e){
            }
        };

        return dictContent;
    }
}
